namespaceGQCP{


/*
*CONSTRUCTORS
*/

SpinUnresolvedONV::SpinUnresolvedONV(constsize_tM,constsize_tN,constsize_tunsigned_representation):
SpinUnresolvedONV(M,N){

this->unsigned_representation=unsigned_representation;
this->updateOccupationIndices();//throwserroriftherepresentationandNarenotcompatible
}


SpinUnresolvedONV::SpinUnresolvedONV(constsize_tM,constsize_tN):
M{M},
N{N},
occupied_indices(N,0){}


/*
*NAMEDCONSTRUCTORS
*/

SpinUnresolvedONVSpinUnresolvedONV::FromString(conststd::string&string_representation){

boost::dynamic_bitset<>intermediate_bitset{string_representation};//theleastsignificantbithasthehighestpositioninthestring
constautoM=intermediate_bitset.size();
constautoN=intermediate_bitset.count();

constautounsigned_representation=static_cast<size_t>(intermediate_bitset.to_ulong());

returnSpinUnresolvedONV(M,N,unsigned_representation);
}


SpinUnresolvedONVSpinUnresolvedONV::FromOccupiedIndices(conststd::vector<size_t>&occupied_indices,constsize_tM){

//Generatethecorrespondingunsignedrepresentationandusethatconstructor.
size_tunsigned_representation=0;
for(constauto&index:occupied_indices){
unsigned_representation+=std::pow(2,index);
}

constsize_tN=occupied_indices.size();
returnSpinUnresolvedONV(M,N,unsigned_representation);
}


SpinUnresolvedONVSpinUnresolvedONV::GHF(constsize_tM,constsize_tN,constVectorX<double>&orbital_energies){

//TheGHFONVisthatoneinwhichtheNspinorswiththelowestenergyareoccupied.

//Createanarraythatcontainstheindicesofthespinorswithascendingenergy.
std::vector<size_t>indices(M);//zero-initializedwithMelements
std::iota(indices.begin(),indices.end(),0);//startwith0

//Sorttheindicesaccordingtotheorbitalenergies.
std::stable_sort(indices.begin(),indices.end(),[&orbital_energies](constsize_ti,constsize_tj){returnorbital_energies(i)<orbital_energies(j);});

conststd::vector<size_t>occupied_indices{indices.begin(),indices.begin()+N};//thefirstNelements
returnSpinUnresolvedONV::FromOccupiedIndices(occupied_indices,M);
}


/*
*OPERATORS
