std::ostream&operator<<(std::ostream&os,constSpinUnresolvedONV&onv){
returnos<<onv.asString();
}


boolSpinUnresolvedONV::operator==(constSpinUnresolvedONV&other)const{
returnthis->unsigned_representation==other.unsigned_representation&&this->M==other.M;//thisensuresthatN,Mandrepresentationareequal
}


boolSpinUnresolvedONV::operator!=(constSpinUnresolvedONV&other)const{
return!(this->operator==(other));
}


/*
*PUBLICMETHODS
*/

boolSpinUnresolvedONV::annihilate(constsize_tp){

if(this->isOccupied(p)){
constsize_toperator_string=1U<<p;
this->unsigned_representation&=~operator_string;
returntrue;
}else{
returnfalse;
}
}


boolSpinUnresolvedONV::annihilate(constsize_tp,int&sign){

if(this->annihilate(p)){//wehavetofirstcheckifwecanannihilatebeforeapplyingthephasefactor
sign*=this->operatorPhaseFactor(p);
returntrue;
}else{
returnfalse;
}
}


boolSpinUnresolvedONV::annihilateAll(conststd::vector<size_t>&indices){

if(this->areOccupied(indices)){//onlyifallindicesareoccupied,wewillannihilate
for(constauto&index:indices){
this->annihilate(index);
}
returntrue;
}else{
returnfalse;
}
}


boolSpinUnresolvedONV::annihilateAll(conststd::vector<size_t>&indices,int&sign){

if(this->areOccupied(indices)){//onlyifallindicesareoccupied,wewillannihilate
for(constauto&index:indices){
this->annihilate(index,sign);
}
returntrue;
}else{
returnfalse;
}
}


boolSpinUnresolvedONV::areOccupied(conststd::vector<size_t>&indices)const{

for(constauto&index:indices){
if(!this->isOccupied(index)){
returnfalse;
}
}

//Onlyifallindiceshavebeentestedtobeoccupied,wecanreturntrue
returntrue;
}


boolSpinUnresolvedONV::areUnoccupied(conststd::vector<size_t>&indices)const{

for(constauto&index:indices){
if(this->isOccupied(index)){
returnfalse;
}
}

//Onlyifallindiceshavebeentestedtobeunoccupied,wecanreturntrue
returntrue;
}


std::stringSpinUnresolvedONV::asString()const{

boost::dynamic_bitset<>intermediate_bitset{this->M,this->unsigned_representation};
std::stringtext;//thestringthatwillcontainthetextualrepresentationofthisspin-unresolvedONV

boost::to_string(intermediate_bitset,text);
returntext;
}


doubleSpinUnresolvedONV::calculateProjection(constSpinUnresolvedONV&onv_on,constGTransformation<double>&C_of,constGTransformation<double>&C_on,constSquareMatrix<double>&S)const{

//Makeareferencecopyinordertoimprovereadibilityofthefollowingcode.
constauto&onv_of=*this;


//Calculatetherawmatrixrepresentationofthetransformationbetweenthespinorbases,sincewe'regoingtohavetosliceitsrowsandcolumns.
MatrixX<double>U=C_on.matrix().adjoint()*S*C_of.matrix();


//U'scolumnsshouldbetheonesoccupiedinthe'of'-ONV.
//U'srowsshouldbetheonesoccupiedinthe'on'-ONV.
//WhilewaitingforEigen3.4torelease(whichhasbetterslicingAPIs),we'llremovetheUNoccupiedrows/columns.
constautounoccupied_indices_of=onv_of.unoccupiedIndices();
constautounoccupied_indices_on=onv_on.unoccupiedIndices();

U.removeColumns(unoccupied_indices_of);
U.removeRows(unoccupied_indices_on);


//Therequestedoverlapelementisthedeterminantoftheresultingmatrix.
returnU.determinant();
}


size_tSpinUnresolvedONV::countNumberOfDifferences(constSpinUnresolvedONV&other)const{
return__builtin_popcountl(this->unsigned_representation^other.unsigned_representation);
}


boolSpinUnresolvedONV::create(constsize_tp){

if(!this->isOccupied(p)){
constsize_toperator_string=1U<<p;
this->unsigned_representation^=operator_string;
returntrue;
}else{
returnfalse;
}
}


boolSpinUnresolvedONV::create(constsize_tp,int&sign){

if(this->create(p)){//wehavetofirstcheckifwecancreatebeforeapplyingthephasefactor
sign*=this->operatorPhaseFactor(p);
returntrue;
}else{
returnfalse;
}
}


boolSpinUnresolvedONV::createAll(conststd::vector<size_t>&indices){

if(this->areUnoccupied(indices)){
for(constauto&index:indices){
this->create(index);
}
returntrue;
}else{
returnfalse;
}
}


boolSpinUnresolvedONV::createAll(conststd::vector<size_t>&indices,int&sign){

if(this->areUnoccupied(indices)){
for(constauto&index:indices){
this->create(index,sign);
}
returntrue;
}else{
returnfalse;
}
}


std::vector<size_t>SpinUnresolvedONV::findDifferentOccupations(constSpinUnresolvedONV&other)const{

constsize_tdifferences=this->unsigned_representation^other.unsigned_representation;
size_toccupied_differences=differences&this->unsigned_representation;//thisholdsallindicesoccupiedinthis,butunoccupiedinother

constsize_tnumber_of_occupied_differences=__builtin_popcountl(occupied_differences);
std::vector<size_t>positions;
positions.reserve(number_of_occupied_differences);

//Findthepositionsofthesetbitsinoccupied_differences
for(size_tcounter=0;counter<number_of_occupied_differences;counter++){//countsthenumberofoccupieddifferenceswehavealreadyencountered
constsize_tposition=__builtin_ctzl(occupied_differences);//counttrailingzeros
positions.push_back(position);

occupied_differences^=occupied_differences&-occupied_differences;//annihilatetheleastsignificantsetbit
}

returnpositions;
}


std::vector<size_t>SpinUnresolvedONV::findMatchingOccupations(constSpinUnresolvedONV&other)const{

size_tmatches=this->unsigned_representation&other.unsigned_representation;

constsize_tnumber_of_occupied_matches=__builtin_popcountl(matches);
std::vector<size_t>positions;
positions.reserve(number_of_occupied_matches);


//Findthepositionsofthesetbitsinoccupied_differences
for(size_tcounter=0;counter<number_of_occupied_matches;counter++){//countsthenumberofoccupieddifferenceswehavealreadyencountered
constsize_tposition=__builtin_ctzl(matches);//counttrailingzeros
positions.push_back(position);

matches^=matches&-matches;//annihilatetheleastsignificantsetbit
}

returnpositions;
}


voidSpinUnresolvedONV::forEach(conststd::function<void(constsize_t)>&callback)const{

//LoopovereveryelectroninthisONVandretrievetheindexofthespinorthatitoccupies.
for(size_te=0;e<this->numberOfElectrons();e++){
constsize_tp=this->occupationIndexOf(e);
callback(p);
}//electronindexloop
}


voidSpinUnresolvedONV::forEach(conststd::function<void(constsize_t,constsize_t)>&callback)const{

//LoopovereveryelectroninthisONVandretrievetheindexofthespinorthatitoccupies.
for(size_te1=0;e1<this->numberOfElectrons();e1++){
constsize_tp=this->occupationIndexOf(e1);

//LoopovereverydifferentelectroninthisONVandretrievetheindexofthespinorthatitoccupies.
for(size_te2=0;e2<e1;e2++){
constsize_tq=this->occupationIndexOf(e2);

callback(p,q);
}//electron2indexloop
}//electron1indexloop
}


boolSpinUnresolvedONV::isOccupied(constsize_tp)const{

if(p>this->M-1){
throwstd::invalid_argument("SpinUnresolvedONV::isOccupied(size_t):Theindexisoutofthebitsetbounds");
}

constsize_toperator_string=1U<<p;
returnthis->unsigned_representation&operator_string;
}


boolSpinUnresolvedONV::isUnoccupied(constsize_tp)const{
return!this->isOccupied(p);
}


intSpinUnresolvedONV::operatorPhaseFactor(constsize_tp)const{

if(p==0){//wecan'tgivethistothis->slice(0,0)
return1;
}
constsize_tm=__builtin_popcountl(this->slice(0,p));//countthenumberofsetbitsintheslice[0,p-1]

if(m%2==0){//evennumberofelectrons:phasefactor(+1)
return1;
}else{//oddnumberofelectrons:phasefactor(-1)
return-1;
}
}


OrbitalSpaceSpinUnresolvedONV::orbitalSpace()const{

//Createanoccupied-virtualorbitalspace.
returnOrbitalSpace(this->occupiedIndices(),this->unoccupiedIndices());
}


voidSpinUnresolvedONV::replaceRepresentationWith(constsize_tunsigned_representation){

this->unsigned_representation=unsigned_representation;
this->updateOccupationIndices();
}


size_tSpinUnresolvedONV::slice(constsize_tindex_start,constsize_tindex_end)const{

//First,dosomechecks
if(index_end<=index_start){
throwstd::invalid_argument("SpinUnresolvedONV::slice(size_t,size_t):index_endshouldbelargerthanindex_start.");
}

if(index_end>this->M+1){
throwstd::invalid_argument("SpinUnresolvedONV::slice(size_t,size_t):Thelastslicingindexindex_endcannotbegreaterthanthenumberofspatialorbitalsM.");
}

//Theunionoftheseconditionsalsoincludethecasethatindex_start>this->M


//Shiftbitstotheright
constsize_tu=this->unsigned_representation>>index_start;


//Createthecorrectmask
constsize_tmask_length=index_end-index_start;
constsize_tmask=((1U)<<mask_length)-1;


//Usethemask
returnu&mask;
}


std::vector<size_t>SpinUnresolvedONV::unoccupiedIndices()const{

//Createavectorcontainingallindices.
std::vector<size_t>all_indices(this->M);
std::iota(all_indices.begin(),all_indices.end(),0);//fillall_indiceswithincreasingnumbers,startingby0

//Theunoccupiedindicesare{allindices}\{occupiedindices}.
std::vector<size_t>unoccupied_indices;
std::set_difference(all_indices.begin(),all_indices.end(),this->occupied_indices.begin(),this->occupied_indices.end(),
std::inserter(unoccupied_indices,unoccupied_indices.begin()));

returnunoccupied_indices;
}


voidSpinUnresolvedONV::updateOccupationIndices(){

size_trepresentation_copy=this->unsigned_representation;
intelectron_index=0;

while(representation_copy!=0){
this->occupied_indices[electron_index]=__builtin_ctzl(representation_copy);//retrievesoccupationindex
electron_index++;

representation_copy^=(representation_copy&-representation_copy);//fliptheleastsignificantbit
}

if(electron_index!=this->N){
throwstd::invalid_argument("SpinUnresolvedONV::updateOccupationIndices():Thecurrentrepresentationandelectroncountarenotcompatible");
}
}


