/*
*MARK:Constructors
*/

SpinUnresolvedONVBasis::SpinUnresolvedONVBasis(constsize_tM,constsize_tN):
M{M},
N{N}{

//Setupthevertexweightsfortheaddressingschemeforafullspin-unresolvedONVbasis.ThisaddressingschemeistakenfromHelgaker,JÃ¸rgensen,Olsen(2000).

//Createazeromatrixofdimensions(M+1)x(N+1)
this->vertex_weights=std::vector<std::vector<size_t>>(M+1,std::vector<size_t>(N+1,0));

//M=5N=2
//[000]
//[000]
//[000]
//[000]
//[000]
//[000]


//Thelargest(reverselexical)stringistheonethatincludesthefirst(M-N+1)verticesofthefirstcolumn
//Thisisbecauseeveryverticalmovefrom(p,m)to(p+1,m+1)correspondsto"orbitalp+1isunoccupied".
//Therefore,thelargestreverselexicalstringistheonewherethefirst(M-N)orbitalsareunoccupied.
//Thismeansthatthereshouldbe(M-N)verticalmovesfrom(0,0).
//Therefore,wemayonlysettheweightsoffirst(M-N+1)verticesofthefirstcolumnto1.
for(size_tp=0;p<M-N+1;p++){
this->vertex_weights[p][0]=1;
}

//M=5N=2
//[100]
//[100]
//[100]
//[100]
//[000]
//[000]


//Therecurrencerelationforthevertexweightsisasfollows:
//Everyelementisthesumofthevaluesoftheelementverticallyaboveandtheelementleftdiagonallyabove.
//W(p,m)=W(p-1,m)+W(p-1,m-1)

for(size_tm=1;m<N+1;m++){
for(size_tp=m;p<(M-N+m)+1;p++){
this->vertex_weights[p][m]=this->vertex_weights[p-1][m]+this->vertex_weights[p-1][m-1];
}
}

//M=5N=2
//[100]
//[110]
//[121]
//[133]
//[046]
//[0010]
}


/*
*MARK:Basicinformation
*/

size_tSpinUnresolvedONVBasis::calculateDimension(constsize_tM,constsize_tN){
constautodim_double=boost::math::binomial_coefficient<double>(static_cast<unsigned>(M),static_cast<unsigned>(N));
try{
returnboost::numeric::converter<size_t,double>::convert(dim_double);
}catch(boost::numeric::bad_numeric_cast&e){
throwstd::overflow_error("SpinUnresolvedONVBasis::calculateDimension(constsize_t,constsize_t):"+std::string(e.what()));
}
}


/*
*MARK:Addressingscheme,addresscalculationsandONVmanipulations
*/


size_tSpinUnresolvedONVBasis::arcWeight(constsize_tp,constsize_tn)const{

//Arcweightsandvertexweightsarerelated.ThisrelationisfoundinHelgaker,JÃ¸rgensen,Olsen(2000)inchapter11.3.6.
returnthis->vertexWeight(p,n+1);
}


size_tSpinUnresolvedONVBasis::addressOf(constsize_trepresentation)const{

//AnimplementationoftheformulainHelgaker,startingtheaddressingcountfromzero
size_tcopy=representation;
size_taddress=0;
size_telectron_count=0;//countsthenumberofelectronsinthespinstringuptoorbitalp
while(copy!=0){//wewillremovetheleastsignificantbiteachloop,wearefinishedwhennobitsareleft
size_tp=__builtin_ctzl(copy);//pistheorbitalindexcounter(startingfrom1)
electron_count++;//eachbitisanelectronhenceweaddituptotheelectroncount
address+=this->vertexWeight(p,electron_count);
copy^=copy&-copy;//fliptheleastsignificantbit
}
returnaddress;
}


size_tSpinUnresolvedONVBasis::nextPermutationOf(constsize_trepresentation)const{

//tgetsthis->representation'sleastsignificant0bitssetto1
unsignedlongt=representation|(representation-1UL);

//Nextsetto1themostsignificantbittochange,
//setto0theleastsignificantones,andaddthenecessary1bits.
return(t+1UL)|(((~t&(t+1UL))-1UL)>>(__builtin_ctzl(representation)+1UL));
}


size_tSpinUnresolvedONVBasis::representationOf(size_taddress)const{
size_trepresentation=0;
if(this->numberOfElectrons()!=0){
representation=0;
size_tm=this->numberOfElectrons();//countsthenumberofelectronsinthespinstringuptoorbitalp

for(size_tp=this->numberOfOrbitals();p>0;p--){//pisanorbitalindex
size_tweight=vertexWeight(p-1,m);

if(weight<=address){//thealgorithmcanmovediagonally,sowefoundanoccupiedorbital
address-=weight;
representation|=((1)<<(p-1));//setthe(p-1)thbit:see(https://stackoverflow.com/a/47990)

m--;//sincewefoundanoccupiedorbital,wehaveoneelectronless
if(m==0){
break;
}
}
}
}
returnrepresentation;
}


SpinUnresolvedONVSpinUnresolvedONVBasis::constructONVFromAddress(constsize_taddress)const{

SpinUnresolvedONVonv{this->numberOfOrbitals(),this->numberOfElectrons()};
this->transformONVCorrespondingToAddress(onv,address);
returnonv;
}


voidSpinUnresolvedONVBasis::transformONVToNextPermutation(SpinUnresolvedONV&onv)const{

onv.replaceRepresentationWith(this->nextPermutationOf(onv.unsignedRepresentation()));
}


voidSpinUnresolvedONVBasis::transformONVCorrespondingToAddress(SpinUnresolvedONV&onv,constsize_taddress)const{

onv.replaceRepresentationWith(this->representationOf(address));
}


/*
*MARK:Couplings
*/


size_tSpinUnresolvedONVBasis::countOneElectronCouplings(constSpinUnresolvedONV&onv)const{

size_tV=this->numberOfOrbitals()-this->numberOfElectrons();//numberofvirtualorbitals
size_tcoupling_count=0;

for(size_te1=0;e1<this->numberOfElectrons();e1++){
size_tp=onv.occupationIndexOf(e1);
coupling_count+=(V+e1-p);//numberofvirtualswithanindexlargerthanp
}

returncoupling_count;
}


size_tSpinUnresolvedONVBasis::countTotalOneElectronCouplings()const{

constautoM=this->numberOfOrbitals();
constautoN=this->numberOfElectrons();

return(M-N)*N*(this->dimension());
}


size_tSpinUnresolvedONVBasis::countTotalTwoElectronCouplings()const{

constautoM=this->numberOfOrbitals();
constautoN=this->numberOfElectrons();

size_ttwo_electron_permutation=0;//alldistributionsfortwoelectronsoverthevirtualorbitals
if(M-N>=2){
two_electron_permutation=SpinUnresolvedONVBasis::calculateDimension(M-N,2)*N*(N-1)*(this->dimension())/2;
}

returntwo_electron_permutation+countTotalOneElectronCouplings();
}


size_tSpinUnresolvedONVBasis::countTwoElectronCouplings(constSpinUnresolvedONV&onv)const{

constautoM=this->numberOfOrbitals();
constautoN=this->numberOfElectrons();

size_tV=M-N;//numberofvirtualorbitals
size_tcoupling_count=0;

for(size_te1=0;e1<N;e1++){

size_tp=onv.occupationIndexOf(e1);
coupling_count+=(V+e1-p);//oneelectronpart

for(size_te2=e1+1;e2<N;e2++){

size_tq=onv.occupationIndexOf(e2);
size_tcoupling_count2=(V+e2-q);
coupling_count+=(V-coupling_count2)*coupling_count2;

if(coupling_count2>1){
coupling_count+=SpinUnresolvedONVBasis::calculateDimension(coupling_count2,2);
}
}
}

returncoupling_count;
}


std::vector<Eigen::SparseMatrix<double>>SpinUnresolvedONVBasis::calculateOneElectronCouplings()const{

size_tK=this->numberOfOrbitals();
size_tN=this->numberOfElectrons();
size_tdim=this->dimension();

std::vector<std::vector<Eigen::Triplet<double>>>sparse_entries(K*(K+1)/2);
std::vector<Eigen::SparseMatrix<double>>sparse_matrices(K*(K+1)/2,Eigen::SparseMatrix<double>(dim,dim));

if(N==0){
returnsparse_matrices;
}

//Reserveappropriatenumberofentries
size_treservation_size=SpinUnresolvedONVBasis::calculateDimension(K-1,N-1);
for(size_tp=0;p<K;p++){
sparse_entries[p*(K+K+1-p)/2].reserve(reservation_size);
for(size_tq=p+1;q<K;q++){
sparse_entries[p*(K+K+1-p)/2+q-p].reserve(2*reservation_size);
}
}

SpinUnresolvedONVonv=this->constructONVFromAddress(0);//onvwithaddress0
for(size_tI=0;I<dim;I++){//Iloopsoveralltheaddressesoftheonv
for(size_te1=0;e1<N;e1++){//e1(electron1)loopsoverthe(numberof)electrons
size_tp=onv.occupationIndexOf(e1);//retrievetheindexofagivenelectron
//removetheweightfromtheinitialaddressI,becauseweannihilate
size_taddress=I-this->vertexWeight(p,e1+1);
//Thee2iterationcountsthenumberofencounteredelectronsforthecreationoperator
//Weonlyconsidergreateraddressesthantheinitialone(becauseofsymmetry)
//Henceweonlycountelectronaftertheannihilatedelectron(e1)
sparse_entries[p*(K+K+1-p)/2].emplace_back(I,I,1);
size_te2=e1+1;
size_tq=p+1;
intsign_e2=1;
//performashift
this->shiftUntilNextUnoccupiedOrbital<1>(onv,address,q,e2,sign_e2);
while(q<K){
size_tJ=address+this->vertexWeight(q,e2);

sparse_entries[p*(K+K+1-p)/2+q-p].emplace_back(I,J,sign_e2);
sparse_entries[p*(K+K+1-p)/2+q-p].emplace_back(J,I,sign_e2);

q++;//gotothenextorbital
//performashift
this->shiftUntilNextUnoccupiedOrbital<1>(onv,address,q,e2,sign_e2);
}//(creation)
}//e1loop(annihilation)

//Preventlastpermutation
if(I<dim-1){
this->transformONVToNextPermutation(onv);
}
}

for(size_tk=0;k<K*(K+1)/2;k++){
sparse_matrices[k].setFromTriplets(sparse_entries[k].begin(),sparse_entries[k].end());
}

returnsparse_matrices;
}


/*
*MARK:Iterating
*/

voidSpinUnresolvedONVBasis::forEach(conststd::function<void(constSpinUnresolvedONV&,constsize_t)>&callback)const{

constautodim=this->dimension();
SpinUnresolvedONVonv=this->constructONVFromAddress(0);

//LoopoveralladdressesoftheONVs.
for(size_tI=0;I<dim;I++){

callback(onv,I);

//Preventthelastpermutationfromoccurring,asthiswouldcauseerrors.
if(I<dim-1){
this->transformONVToNextPermutation(onv);
}
}
}


/*
*MARK:Densegeneralizedoperatorevaluations
*/

SquareMatrix<double>SpinUnresolvedONVBasis::evaluateOperatorDense(constScalarGSQOneElectronOperator<double>&f)const{

if(f.numberOfOrbitals()!=this->numberOfOrbitals()){
throwstd::invalid_argument("SpinUnresolvedONVBasis::evaluateOperatorDense(constScalarGSQOneElectronOperator<double>&):ThenumberoforbitalsofthisONVbasisandthegivenone-electronoperatorareincompatible.");
}

//Initializeacontainerforthedensematrixrepresentation,andfillitwiththegeneralevaluationfunction.
MatrixRepresentationEvaluationContainer<SquareMatrix<double>>container{this->dimension()};
this->evaluate<SquareMatrix<double>>(f,container);

returncontainer.evaluation();
}


SquareMatrix<double>SpinUnresolvedONVBasis::evaluateOperatorDense(constScalarGSQTwoElectronOperator<double>&g)const{

//Inordertoavoidduplicatecode,wechoosetodelegatethismethodtotheevaluationofa`GSQHamiltonian`thatcontainsnocorecontributions.Thisdoesnotaffectperformancesignificantly,becausethebottleneckwillalwaysbetheiterationoverthewholeONVbasis.
constautozero=ScalarGSQOneElectronOperator<double>::Zero(g.numberOfOrbitals());
constGSQHamiltonian<double>hamiltonian{zero,g};

returnthis->evaluateOperatorDense(hamiltonian);
}


SquareMatrix<double>SpinUnresolvedONVBasis::evaluateOperatorDense(constGSQHamiltonian<double>&hamiltonian)const{

if(hamiltonian.numberOfOrbitals()!=this->numberOfOrbitals()){
throwstd::invalid_argument("SpinUnresolvedONVBasis::evaluateOperatorDense(constUSQHamiltonian<double>&):ThenumberoforbitalsofthisONVbasisandthegivenHamiltonianareincompatible.");
}

//Initializeacontainerforthedensematrixrepresentation,andfillitwiththegeneralevaluationfunction.
MatrixRepresentationEvaluationContainer<SquareMatrix<double>>container{this->dimension()};
this->evaluate<SquareMatrix<double>>(hamiltonian,container);

returncontainer.evaluation();
}


/*
*MARK:Denseunrestrictedoperatorevaluations
*/

SquareMatrix<double>SpinUnresolvedONVBasis::evaluateOperatorDense(constScalarUSQOneElectronOperatorComponent<double>&f)const{

//Wemayconvertanunrestrictedcomponentintothegeneralizedrepresentation.
constautof_generalized=ScalarGSQOneElectronOperator<double>::FromUnrestrictedComponent(f);
returnthis->evaluateOperatorDense(f_generalized);
}


SquareMatrix<double>SpinUnresolvedONVBasis::evaluateOperatorDense(constScalarPureUSQTwoElectronOperatorComponent<double>&g)const{

//Wemayconvertanunrestrictedcomponentintothegeneralizedrepresentation.
constautog_generalized=ScalarGSQTwoElectronOperator<double>::FromUnrestrictedComponent(g);
returnthis->evaluateOperatorDense(g_generalized);
}


/*
*MARK:Diagonalgeneralizedoperatorevaluations
*/

VectorX<double>SpinUnresolvedONVBasis::evaluateOperatorDiagonal(constScalarGSQOneElectronOperator<double>&f_op)const{

constautoK=f_op.numberOfOrbitals();

if(K!=this->numberOfOrbitals()){
throwstd::invalid_argument("SpinUnresolvedONVBasis::evaluateOperatorDiagonal(constScalarGSQOneElectronOperator<double>&):ThenumberoforbitalsofthisONVbasisandthegivenoperatorareincompatible.");
}


//Preparesomevariables.
constauto&f=f_op.parameters();

size_tN=this->numberOfElectrons();
size_tdim=this->dimension();

VectorX<double>diagonal=VectorX<double>::Zero(dim);

SpinUnresolvedONVonv=this->constructONVFromAddress(0);//onvwithaddress0
for(size_tI=0;I<dim;I++){//IloopsoveralladdressesinthisONVbasis

if(I>0){
this->transformONVToNextPermutation(onv);
}

for(size_te1=0;e1<N;e1++){//A1(annihilation1)
size_tp=onv.occupationIndexOf(e1);
diagonal(I)+=f(p,p);
}
}

returndiagonal;
};


VectorX<double>SpinUnresolvedONVBasis::evaluateOperatorDiagonal(constScalarGSQTwoElectronOperator<double>&g_op)const{

autoK=g_op.numberOfOrbitals();
if(K!=this->numberOfOrbitals()){
throwstd::invalid_argument("SpinUnresolvedONVBasis::evaluateOperatorDiagonal(constScalarGSQTwoElectronOperator<double>&):ThenumberoforbitalsofthisONVbasisandthegivenoperatorareincompatible.");
}

size_tN=this->numberOfElectrons();
size_tdim=this->dimension();

VectorX<double>diagonal=VectorX<double>::Zero(dim);

constautok=g_op.effectiveOneElectronPartition().parameters();
constauto&g=g_op.parameters();

SpinUnresolvedONVonv=this->constructONVFromAddress(0);//onvwithaddress0
for(size_tI=0;I<dim;I++){//IloopsoveralladdressesinthisONVbasis

if(I>0){
this->transformONVToNextPermutation(onv);
}

for(size_te1=0;e1<N;e1++){//A1(annihilation1)
size_tp=onv.occupationIndexOf(e1);
diagonal(I)+=k(p,p);

for(size_tq=0;q<K;q++){//qloopsoverSOs
if(onv.isOccupied(q)){
diagonal(I)+=0.5*g(p,p,q,q);
}else{
diagonal(I)+=0.5*g(p,q,q,p);
}
}
}
}

returndiagonal;
};


VectorX<double>SpinUnresolvedONVBasis::evaluateOperatorDiagonal(constGSQHamiltonian<double>&sq_hamiltonian)const{

returnthis->evaluateOperatorDiagonal(sq_hamiltonian.core())+this->evaluateOperatorDiagonal(sq_hamiltonian.twoElectron());
};


/*
*MARK:Sparsegeneralizedoperatorevaluations
*/

Eigen::SparseMatrix<double>SpinUnresolvedONVBasis::evaluateOperatorSparse(constScalarGSQOneElectronOperator<double>&f)const{

if(f.numberOfOrbitals()!=this->numberOfOrbitals()){
throwstd::invalid_argument("SpinUnresolvedONVBasis::evaluateOperatorSparse(constScalarGSQOneElectronOperator<double>&):ThenumberoforbitalsoftheONVbasisandtheoperatorareincompatible.");
}

//Initializeacontainerforthesparsematrixrepresentation,andreserveanappropriateamountofmemoryforit.
MatrixRepresentationEvaluationContainer<Eigen::SparseMatrix<double>>container{this->dimension()};
size_tmemory=this->dimension()+this->countTotalOneElectronCouplings();
container.reserve(memory);

//Evaluatetheone-electronoperatorandaddtheevaluationstothesparsematrixrepresentation.
this->evaluate<Eigen::SparseMatrix<double>>(f,container);

//Finalizethecreationofthesparsematrixandreturntheresult.
container.addToMatrix();
returncontainer.evaluation();
}


Eigen::SparseMatrix<double>SpinUnresolvedONVBasis::evaluateOperatorSparse(constScalarGSQTwoElectronOperator<double>&g)const{

//Inordertoavoidduplicatecode,wechoosetodelegatethismethodtotheevaluationofa`GSQHamiltonian`thatcontainsnocorecontributions.Thisdoesnotaffectperformancesignificantly,becausethebottleneckwillalwaysbetheiterationoverthewholeONVbasis.
constautozero=ScalarGSQOneElectronOperator<double>::Zero(g.numberOfOrbitals());
constGSQHamiltonian<double>hamiltonian{zero,g};

returnthis->evaluateOperatorSparse(hamiltonian);
}


Eigen::SparseMatrix<double>SpinUnresolvedONVBasis::evaluateOperatorSparse(constGSQHamiltonian<double>&hamiltonian)const{

if(hamiltonian.numberOfOrbitals()!=this->numberOfOrbitals()){
throwstd::invalid_argument("SpinUnresolvedONVBasis::evaluateOperatorSparse(constGSQHamiltonian<double>&hamiltonian):ThenumberoforbitalsofthisONVbasisandthegivenHamiltonianareincompatible.");
}

//Initializeacontainerforthesparsematrixrepresentation,andreserveanappropriateamountofmemoryforit.
MatrixRepresentationEvaluationContainer<Eigen::SparseMatrix<double>>container{this->dimension()};
size_tmemory=this->dimension()+this->countTotalTwoElectronCouplings();
container.reserve(memory);

//EvaluatetheHamiltonianandaddtheevaluationstothesparsematrixrepresentation.
this->evaluate<Eigen::SparseMatrix<double>>(hamiltonian,container);

//Finalizethecreationofthesparsematrixandreturntheresult.
container.addToMatrix();
returncontainer.evaluation();
}


