Eigen::SparseMatrix<double>SpinUnresolvedONVBasis::evaluateOperatorSparse(constScalarUSQOneElectronOperatorComponent<double>&f)const{

//Wemayconvertanunrestrictedcomponentintothegeneralizedrepresentation.
constautof_generalized=ScalarGSQOneElectronOperator<double>::FromUnrestrictedComponent(f);
returnthis->evaluateOperatorSparse(f_generalized);
